    if (typeof apiKey !== 'string' || apiKey.length <= 2) {
        return apiKey; // Retorna original si no es string o es muy corta
    }
    
    const primero = apiKey.charAt(0);
    const ultimo = apiKey.charAt(apiKey.length - 1);
    const asteriscos = '*'.repeat(apiKey.length - 2);
    
    return primero + asteriscos + ultimo;
}

