// Obtenemos los datos para la nueva entrada del historial.
const newName = context.item.data?.['name'];
const newId = context.item.data?.['id'];
const newDate = context.item.data?.['created_at'];
// (NUEVO) Definimos el tipo estático para esta ejecución específica.
const newType = 'Carpeta';


// --- PASO 2: Crear el nuevo objeto (la nueva entrada del historial) ---

// Validamos que tenemos los datos necesarios antes de continuar.
if (!newName || !newId || !newDate) {
  // Si falta algún dato esencial, devolvemos el historial sin cambios.
  return existingHistorial;
}

// Creamos la nueva entrada AÑADIENDO el campo 'Tipo'.
const newEntry = {
  name: newName,
  id: newId,
  fecha: newDate,
  Tipo: newType // Aquí se incluye el tipo solo para este nuevo elemento.
};


// --- PASO 3: Eliminar duplicados del historial existente ---

// Esta lógica no cambia. Sigue funcionando perfectamente porque solo
// se fija en el 'id' para filtrar.
const historialSinDuplicados = existingHistorial.filter(item => item.id !== newId);


// --- PASO 4: Añadir la nueva entrada al PRINCIPIO del array filtrado ---

// La nueva entrada (que ahora tiene el campo 'Tipo') se añade al principio.
// Los elementos antiguos de 'historialSinDuplicados' no se modifican.
const updatedHistorial = [newEntry, ...historialSinDuplicados];


// --- PASO 5: Asegurar que el array tenga un MÁXIMO de 3 elementos ---

// Esta lógica no cambia.
const finalHistorial = updatedHistorial.slice(0, 3);


// --- PASO 6: Devolver el resultado final ---

// Devolvemos el array actualizado.
return finalHistorial;

