// Usamos el operador de encadenamiento opcional (?.) para evitar errores si la colección
// aún no se ha cargado. Si es undefined, usamos un array vacío [].
const items = collections['bc9e894f-b9f6-4789-a74a-d07927ad48a4']?.data || [];

// 2. Obtener el término de búsqueda de la variable
// Si la variable está vacía o no existe, usamos una cadena vacía '' como valor por defecto.
const searchTerm = variables['614dd645-0d3b-4886-9507-2c3786c6d7af'] || '';

// 3. Si no hay término de búsqueda, devolver todos los items sin filtrar.
// Esto es una optimización para no procesar la lista innecesariamente.
if (!searchTerm) {
  return items;
}

// 4. Preparar el término de búsqueda para una comparación que no distinga mayúsculas/minúsculas.
// Lo convertimos a minúsculas una sola vez antes de empezar a filtrar para ser más eficientes.
const query = searchTerm.toLowerCase();

// 5. Filtrar el array de items
const filteredItems = items.filter(item => {
  // Verificación de seguridad: nos aseguramos de que el item exista y tenga la propiedad 'Titulo'.
  // Esto previene errores si algún dato en la colección viene incompleto.
  if (!item || !item.Titulo) {
    return false; // Si no tiene Título, no puede coincidir, así que lo descartamos.
  }

  // Convertimos el 'Titulo' del item a minúsculas y comprobamos si INCLUYE el término de búsqueda.
  return item.Titulo.toLowerCase().includes(query);
});

// 6. Devolver el resultado del filtro
return filteredItems;
