// 1. Obtener el objeto completo de datos del workflow
const workflowData = context?.workflow?.data || {};

// 2. Mostrar datos para depuración (eliminar en producción)
console.log("Datos completos del workflow:", workflowData);
wwLib.wwNotification.open({
  text: `Datos recibidos: ${JSON.stringify(workflowData)}`,
  status: 'info',
  duration: 5000
});

// 3. Obtener categorías de forma segura
const categorias = [];
if (workflowData.categorias) {
  // Manejar diferentes formatos (array, string, objeto)
  if (Array.isArray(workflowData.categorias)) {
    categorias.push(...workflowData.categorias);
  } else if (typeof workflowData.categorias === 'string') {
    categorias.push(workflowData.categorias);
  } else if (workflowData.categorias.value) {
    categorias.push(workflowData.categorias.value);
  }
}

// 4. Obtener opciones del multiselect
const opciones = [];
if (workflowData.Multiselect) {
  // Manejar diferentes formatos
  if (Array.isArray(workflowData.Multiselect)) {
    opciones.push(...workflowData.Multiselect);
  } else if (workflowData.Multiselect.value) {
    // Si es un objeto con propiedad value
    if (Array.isArray(workflowData.Multiselect.value)) {
      opciones.push(...workflowData.Multiselect.value);
    } else {
      opciones.push(workflowData.Multiselect.value);
    }
  } else if (typeof workflowData.Multiselect === 'string') {
    opciones.push(workflowData.Multiselect);
  }
}

// 5. Crear mensaje descriptivo
const mensaje = `Ping - ${opciones.length > 0 
  ? `Opciones seleccionadas: ${opciones.join(', ')}` 
  : 'Sin opciones seleccionadas'}`;

// 6. Preparar datos para enviar
const datosParaEnviar = {
  message: mensaje,
  categorias: categorias,
  opciones_multiselect: opciones
};

// 7. Configurar la solicitud
const url = 'https://appwebhook.clivo.net/webhook/ResuelveConsulta';

try {
  // 8. Realizar el POST
  const response = await fetch(url, {
    method: 'POST',
    headers: {'Content-Type': 'application/json'},
    body: JSON.stringify(datosParaEnviar)
  });

  // 9. Manejar respuesta
  if (!response.ok) {
    const errorText = await response.text();
    console.error(`Error ${response.status}: ${errorText}`);
    wwLib.wwNotification.open({
      text: `Error del servidor: ${response.status}`,
      status: 'danger'
    });
    return {success: false, status: response.status};
  }

  return await response.json();

} catch (error) {
  console.error('Error de conexión:', error);
  wwLib.wwNotification.open({
    text: 'Error: No se pudo conectar con el servicio',
    status: 'danger'
  });
  return {
    success: false,
    error: error.message,
    datosEnviados: datosParaEnviar // Para depuración
  };
}

